const projHTML=()=>{fetch("/res/projects.json").then(e=>{if(!e.ok)throw Error(e.statusText);return e.json()}).then(e=>e.sort((e,t)=>{let a=new Date(e.date).getTime();return new Date(t.date).getTime()-a})).then(e=>e.map((e,t)=>{let a=e.date.split(" "),n=`${a[0]} ${a[2]}`;return`<article class="smallProjWrap">\n            <span>${"Music"===e.type?'<i class="fas fa-headphones"></i>':"Software"===e.type?'<i class="fas fa-laptop"></i>':'<i class="fas fa-microphone"></i>'} <b>${e.type.toLowerCase()}</b></span>\n            <a href=${e.link}\n            target="_blank"\n            tabindex="${t+5}"\n            rel="noopener noreferrer" >\n                <div class="smallProj">\n                    <p class="smallProjTitle">${e.name.toLowerCase()}</p>\n                    <p class="smallProjDescr">${e.description}</p>\n                </div>\n            </a>\n            <time datetime="${new Date(e.date).toISOString()}">${n.toLowerCase()}</time>\n        </article>\n        `})).then(e=>{document.getElementById("projects").innerHTML=e.join(" ")}).catch(console.log)};export{projHTML};